import React, { useState } from "react";
import Title from "./components/Title";
import NewEventForm from "./components/NewEventForm";
import mando from "../src/assets/img/mando.png";
import IconButton from "./components/IconButton";
import { IoAddCircle } from "react-icons/io5";
import { VscEye } from "react-icons/vsc";
import { VscEyeClosed } from "react-icons/vsc";
import styles from "./components/IconButton.module.css";
import "./index.css";
import SmoothList from "react-smooth-list";

function App() {
  const [showModal, setShowModal] = useState(false);
  const [showEvents, setShowEvents] = useState(true);
  const [events, setEvents] = useState([]);

  const addEvent = (event) => {
    setEvents((prevEvents) => {
      return [event, ...prevEvents];
    });
    setShowModal(false);
  };

  // const handleClick = (id) => {
  //   setEvents((prevEvents) => {
  //     return prevEvents.filter((event) => id !== event.id);
  //   });
  // };

  return (
    <div
      style={{
        display: "block",
        backgroundColor: "green",
        height: "100%",
        overflow: "auto",
        maxHeight: "100vh",
      }}
    >
      {/* Header */}
      <Title>
        <div className="logo">
          <img
            className="logo"
            src={mando}
            alt="Mandalorian Helmet"
            width="30px"
            height="30px"
          />
          <h1 className="title">Man-Do Notes</h1>
        </div>
        <div className="options">
          <IconButton
            icon={<IoAddCircle className={styles.icon} />}
            text="New Event"
            onClick={() => setShowModal(true)}
          />
          {events.length !== 0 && (
            <IconButton
              icon={
                showEvents ? (
                  <VscEyeClosed className={styles.icon} />
                ) : (
                  <VscEye className={styles.icon} />
                )
              }
              text={showEvents ? "Hide Events" : "Show Events"}
              onClick={() => setShowEvents(!showEvents)}
            />
          )}
        </div>
      </Title>

      {/* Modal*/}

      <div style={{ display: "flex", backgroundColor: "blue", height: "100%" }}>
        {showModal && <NewEventForm addEvent={addEvent} />}
        {events &&
          showEvents &&
          events.map((event, index) => (
            <SmoothList transitionDuration={500}>
              <NewEventForm event={event} key={index} />
            </SmoothList>
          ))}
      </div>
    </div>
  );
}

export default App;

