import "./App.css";
import { useState } from "react";

function App() {
  const [name, setName] = useState("Suresh");
  const [events, setEvents] = useState([
    { title: "Jason wedding anniversary!", id: "1" },
    { title: "Raghu birthday!", id: "2" },
  ]);

  const onClickHandler = (id) => {
    setEvents((prevState) => {
      console.log(prevState);
      setName((pState) => {
        console.log("setname prev state is ", pState);
        return `${pState}+Kumar`;
      });
      return prevState.filter((item) => {
        return id !== item.id;
      });
    });
  };

  return (
    <div className="App">
      <h1>Hi there! my name is {name}</h1>
      {events.map((item, index) => (
        <div key={item.id}>
          <p>
            {index} - {item.title}
          </p>
          <button onClick={() => onClickHandler(item.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default App;
