import React, { useState } from "react";
import Title from "./components/Title";
import Modal from "./components/Modal";
import EventList from "./components/EventList";
import NewEventForm from "./components/NewEventForm";
import mando from "../src/assets/img/mando.png";
import IconButton from "./components/IconButton";
import { IoAddCircle } from "react-icons/io5";
import { VscEye } from "react-icons/vsc";
import { VscEyeClosed } from "react-icons/vsc";
import styles from "./components/IconButton.module.css";

function App() {
  const [showModal, setShowModal] = useState(false);
  const [showEvents, setShowEvents] = useState(true);
  const [events, setEvents] = useState([]);

  const addEvent = (event) => {
    setEvents((prevEvents) => {
      return [...prevEvents, event];
    });
    setShowModal(false);
  };

  const handleClick = (id) => {
    setEvents((prevEvents) => {
      return prevEvents.filter((event) => id !== event.id);
    });
  };

  return (
    <div className="App">
      {/* Header */}
      <Title>
        <img
          className="logo"
          src={mando}
          alt="Mandalorian Helmet"
          width="30px"
          height="30px"
        />
        <h1 className="title">Mando Events</h1>
        <IconButton
          icon={<IoAddCircle className={styles.icon} />}
          text="New Event"
          onClick={() => setShowModal(true)}
        />
        <IconButton
          icon={<IoAddCircle className={styles.icon} />}
          text="New Event"
          onClick={() => setShowModal(true)}
        />
        <IconButton
          icon={<IoAddCircle className={styles.icon} />}
          text="New Event"
          onClick={() => setShowModal(true)}
        />
      </Title>

      {/* Left Column with Buttons */}
      <Title className="left-block">
        <IconButton
          icon={<IoAddCircle className={styles.icon} />}
          text="New Event"
          onClick={() => setShowModal(true)}
        />
        <br />
        {events.length !== 0 && (
          <IconButton
            icon={
              showEvents ? (
                <VscEyeClosed className={styles.icon} />
              ) : (
                <VscEye className={styles.icon} />
              )
            }
            text={showEvents ? "Hide Events" : "Show Events"}
            onClick={() => setShowEvents(!showEvents)}
          />
        )}
      </Title>
      {/**/}

      {showEvents && <EventList events={events} handleClick={handleClick} />}

      {/* Modal*/}
      {showModal && (
        <Modal closeModal={() => setShowModal(false)}>
          <NewEventForm addEvent={addEvent} />
        </Modal>
      )}
    </div>
  );
}

export default App;
