import { useState } from "react";
import "./NewEventForm.css";
import { AiFillCheckCircle } from "react-icons/ai";
import { TiPencil } from "react-icons/ti";
import moment from "moment";

export default function NewEventForm({ addEvent, event }) {
  const [note, setNote] = useState("");

  const resetForm = () => {
    setNote("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const event = {
      note: note,
      date: moment().format("LL"),
      id: Math.floor(Math.random() * 10000),
    };
    console.log(event);
    addEvent(event);
    resetForm();
  };

  return (
    <form className="new-event-form" onSubmit={handleSubmit}>
      <label>
        <textarea
          name="note"
          rows="5"
          cols="30"
          placeholder="This is Man-Do note..."
          required
          onChange={(e) => setNote(e.target.value)}
          value={event && event.note}
          disabled={event ? true : false}
        />
        <div className="bottom-card">
          <span>
            {moment(new Date()).format("LL")} @
            {moment(new Date()).format("HH:mm")}
          </span>
          <button style={{ margin: 0, color: "black" }}>
            {event ? (
              <TiPencil className="logo" />
            ) : (
              <AiFillCheckCircle className="logo" />
            )}
          </button>
        </div>
      </label>
    </form>
  );
}
