import "./App.css";
import { useState, useRef, useEffect } from "react";

function App() {
  const h1Ref = useRef(null);
  const [name, setName] = useState("Suresh");
  const [events, setEvents] = useState([
    { title: "Jason wedding anniversary!", id: "1" },
    { title: "Raghu birthday!", id: "2" },
  ]);

  const onClickHandler = (id) => {
    setEvents((prevState) => {
      console.log(prevState);
      setName((pState) => {
        console.log("setname prev state is ", pState);
        return `${pState}+Kumar`;
      });
      return prevState.filter((item) => {
        return id !== item.id;
      });
    });
  };

  useEffect(() => {
    console.log("this is useEffect");
    console.log(h1Ref.current);
    h1Ref.current.value("billaa");
  }, []);

  return (
    <div className="App">
      <h1 ref={h1Ref}>Hi there! my name is {name}</h1>
      {events.map((item, index) => (
        <div key={item.id}>
          <p>
            {index} - {item.title}
          </p>
          <button onClick={() => onClickHandler(item.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default App;
