import "./App.css";
import React, { useState, Fragment } from "react";
import Title from "./components/Title";
import Modal from "./components/Modal";
import IconButton from "./components/IconButton";
import { RiCoupon3Line } from "react-icons/ri";

function App() {
  const [showHandler, setShowHandler] = useState(false);
  const [events, setEvents] = useState([
    { title: "Jason wedding anniversary!", id: "1" },
    { title: "Raghu birthday!", id: "2" },
  ]);

  const onClickHandler = (id) => {
    setEvents((prevState) => {
      return prevState.filter((item) => {
        return id !== item.id;
      });
    });
  };

  const modalBtnCloseHandler = () => {
    setShowHandler(false);
  };

  return (
    <div className="App">
      <Title title="Mando" subtitle="This is the way..." />
      {events.map((item, index) => (
        <Fragment key={item.id}>
          <p>
            {index} - {item.title}
          </p>
          <button onClick={() => onClickHandler(item.id)}>Delete</button>
        </Fragment>
      ))}
      {showHandler && (
        <Modal closeHandler={modalBtnCloseHandler}>
          <h1>Welcome Discount 10% OFF</h1>
          <p>Use code MANDO</p>
        </Modal>
      )}

      <IconButton>
        <RiCoupon3Line
          onClick={() => {
            setShowHandler(true);
          }}
        />
      </IconButton>
    </div>
  );
}

export default App;
